export const uploadPDF = async (file) => {
  const data = new FormData();
  data.append("file", file);
  const res = await fetch("http://localhost:8000/upload", { method: "POST", body: data });
  if (!res.ok) throw new Error("Upload failed");
  return res.json();
};

export const askQuestion = async (docId, question) => {
  const res = await fetch("http://localhost:8000/ask", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ document_id: docId, question }),
  });
  if (!res.ok) throw new Error("Question failed");
  return res.json();
};
